/**
 * Get tier information by tier number
 * @param tier - Tier number (0-12)
 * @returns TierInfo object with full metadata
 */
export function getTierInfo(tier: number): TierInfo {
  return TIER_DEFINITIONS[tier] || TIER_DEFINITIONS[5];  // Default to T5 if invalid
}

/**
 * Get tier name by tier number
 * @param tier - Tier number (0-12)
 * @returns Tier name string
 */
export function getTierName(tier: number): string {
  return TIER_NAMES[tier] || 'Probabilistic';
}

