/**
 * Truth Token Registry Class
 */
export class TruthTokenRegistry {
  private tokens: Map<string, TruthTokenRecord>;
  private tokensByTier: Map<number, TruthTokenRecord[]>;

  constructor() {
    // VALIDATE PATTERNS ON LOAD
    // This prevents invalid patterns (like single-word patterns) from loading
    // Enforces 3-7 word rule that prevents micro-token explosion
    try {
      assertValidRegistry(TRUTH_TOKENS);
      console.log('[Registry] ✅ Pattern validation passed');
    } catch (error: any) {
      console.error('[Registry] ❌ Pattern validation failed');
      throw error; // Crash on startup if patterns are invalid
    }

    this.tokens = new Map(Object.entries(TRUTH_TOKENS));
    this.tokensByTier = new Map();

    // Index by tier
    const allTokens = Array.from(this.tokens.values());
    for (const token of allTokens) {
      if (!this.tokensByTier.has(token.tier)) {
        this.tokensByTier.set(token.tier, []);
      }
      this.tokensByTier.get(token.tier)!.push(token);
    }
  }

  get(symbol: string): TruthTokenRecord | undefined {
    return this.tokens.get(symbol);
  }

  getByTier(tier: number): TruthTokenRecord[] {
    return this.tokensByTier.get(tier) || [];
  }

  search(text: string): TruthTokenRecord[] {
    const normalized = text.toLowerCase();
    const matches: TruthTokenRecord[] = [];

    const allTokens = Array.from(this.tokens.values());
    for (const token of allTokens) {
      // Check if any semantic pattern matches
      for (const pattern of token.semantic_patterns) {
        if (normalized.includes(pattern.toLowerCase())) {
          matches.push(token);
          break;
        }
      }
    }

    return matches;
  }

  /**
   * Design 2: Search with pattern metadata for dominant token selection
   * Returns detailed match information including matched pattern and length
   */
  searchWithMetadata(text: string): TokenMatchResult[] {
    const normalized = text.toLowerCase();
    const matches: TokenMatchResult[] = [];

    const allTokens = Array.from(this.tokens.values());
    for (const token of allTokens) {
      for (const pattern of token.semantic_patterns) {
        const patternLower = pattern.toLowerCase();
        const matchIndex = normalized.indexOf(patternLower);

        if (matchIndex !== -1) {
          matches.push({
            token: token,
            matched_pattern: pattern,
            pattern_length: pattern.split(/\s+/).length,  // Word count
            match_position: matchIndex
          });
          break;  // Only one match per token
        }
      }
    }

    return matches;
  }

  getTierName(tier: number): string {
    return TIER_NAMES[tier] || `T${tier}`;
  }

  count(): number {
    return this.tokens.size;
  }

  getTierDistribution(): Record<number, number> {
    const dist: Record<number, number> = {};
    for (let i = 0; i <= 12; i++) {
      dist[i] = this.getByTier(i).length;
    }
    return dist;
  }

  getGroundedTokens(): TruthTokenRecord[] {
    return Array.from(this.tokens.values()).filter(t => t.grounded);
  }

  getAllTokens(): TruthTokenRecord[] {
    return Array.from(this.tokens.values());
  }
}

// Singleton instance
let _registry: TruthTokenRegistry | null = null;

export function getTruthTokenRegistry(): TruthTokenRegistry {
  if (!_registry) {
    _registry = new TruthTokenRegistry();
  }
  return _registry;
}
